Item.method_code:
Innovator inn = this.getInnovator();

Item relatedTDEs = this.getInnovator().newItem("rs_rel_tdcd_tde", "get");
relatedTDEs.setProperty("source_id", this.getAttribute("id"));
relatedTDEs = relatedTDEs.apply();

// Get user Alias Identity
Item alias = inn.newItem("Alias", "get");
alias.setProperty("source_id", inn.getUserID());
alias.setAttribute("select", "related_id");
alias = alias.apply();
string identity = alias.getProperty("related_id");

// Generate runtime
Item rt = inn.newItem("rs_tdcd_rt", "add");
rt.setProperty("rs_tdcd", this.getAttribute("id"));
rt.setProperty("owned_by_id", identity);
rt.setProperty("rs_current_seed", this.getProperty("rs_seed"));
rt = rt.apply();
if (rt.isError()) {
    return rt;
}

using (CCO.Permissions.GrantIdentity(Aras.Server.Security.Identity.GetByName("Super User"))) {
    for (int i = 0; i < relatedTDEs.getItemCount(); i++) {
        Item relatedTDE = relatedTDEs.getItemByIndex(i).getRelatedItem();
        relatedTDE.setAttribute("action", "rs_tde_execute");
        relatedTDE.setProperty("rs_tdcd", this.getAttribute("id"));
        int cardinality = int.Parse(relatedTDEs.getItemByIndex(i).getProperty("rs_cardinality"));

        for (int j = 0; j < cardinality; j++) {
            Item relatedTDEResult = relatedTDE.apply();
            if (relatedTDEResult.isError()) {
                return relatedTDEResult;
            }
        }
    }
}

rt.setAttribute("action", "delete");
rt = rt.apply();

return this;
